INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
        ('National Park of American Samoa', 'https://www.nps.gov/common/uploads/grid_builder/npsa/crop16_9/CE009D10-1DD8-B71B-0B3872261C0CF20D.jpg?width=1300&quality=90&mode=crop', 'National Historical Park', 'The National Park of American Samoa welcomes you into the heart of the South Pacific, to a world of sights, sounds, and experiences that you will find in no other national park in the United States. Enjoy this unique national park and the welcoming people of American Samoa. We are here to protect its rich culture and natural resources. Come explore them with us!', 1);
INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
    ('War In The Pacific', 'https://www.nps.gov/common/uploads/grid_builder/wapa/crop16_9/7734EB50-C4B8-2FE1-773578DA1DAFEC3A.jpg?width=640&quality=90&mode=crop', 'National Historical Park', 'War in the Pacific National Historical Park was established to commemorate the bravery and sacrifice of those participating in the campaigns of the Pacific Theater of World War II and to conserve and interpret outstanding natural, scenic, and historic values and objects of the island of Guam.', 2);
INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
        ('American Memorial', 'https://www.nps.gov/common/uploads/grid_builder/amme/crop16_9/A7E9CF1D-AE3F-77FB-6A1B356B9230C47D.jpg?width=640&quality=90&mode=crop', 'Park', 'American Memorial Park honors the American and Marianas people who gave their lives during the Marianas Campaign of World War II. At the park, memorials stand in tribute to the courage and sacrifice of the US Servicemen and Chamorro and Carolinian civilians who were killed in the battles between the United States and Japan that took place on Saipan, Tinian, and the Philippine Sea in 1944.', 5);
INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
        ('San Juan', 'https://www.nps.gov/common/uploads/grid_builder/saju/crop16_9/774AA5FF-1DD8-B71B-0B63C1B37E007657.jpg?width=1300&quality=90&mode=crop', 'National Historic Site', 'San Juan National Historic Site preserves stories of great ambition and aspirations. Countries fought for control of this tiny yet strategic island for centuries. Generations of soldiers have lived and worked within the forts. Visitors today are as inspired by these stories as they are by the beauty of the architecture and the ingenuity of design and engineering of this World Heritage Site.', 3);
INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
        ('Buck Island Reef', 'https://www.nps.gov/common/uploads/grid_builder/buis/crop16_9/DAE9A575-1DD8-B71B-0B17525A0253FCD5.jpg?width=640&quality=90&mode=crop', 'National Monument', 'Welcome to Buck Island! Before you come, please watch our 2014 Telly Award winning film "Caribbean Gem." Watching this will catch you up on 50+ years of National Park Service protection and civic engagement. Learn what we do every day to preserve this resource for Virgin Islanders and visitors alike. For the General Management Plan and Environmental Compliance, visit the News page.', 4);
INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
        ('Christiansted', 'https://www.nps.gov/common/uploads/grid_builder/chri/crop16_9/A04B024D-1DD8-B71B-0B3658E13E13D7C2.jpg?width=1300&quality=90&mode=crop', 'National Historic Site', 'Christiansted National Historic Site was established in 1952 as the first unit of the National Park Service in the Virgin Islands of the United States. Covering over seven acres the park tells the stories about Danish economy and ways of life, and the interactions of people in Europe, Africa, West Indies, and North America during the Danish era of occupation of the Virgin Islands (1734-1917).', 4);
INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
        ('Salt River Bay', 'https://www.nps.gov/common/uploads/grid_builder/sari/crop16_9/2A61E5E6-9C76-ACA1-ACE283E8E6307A8C.jpg?width=640&quality=90&mode=crop', 'National Historical Park and Ecological Preserver', 'Salt River Bay National Historical Park and Ecological Preserve uniquely documents the human and natural Caribbean world from the earliest indigenous settlements in the central Caribbean to their clash with seven different colonial European powers to the present day.', 4);
INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
        ('Virgin Islands', 'https://www.nps.gov/common/uploads/grid_builder/viis/crop16_9/4199A821-B2F9-F70A-ADC1ECCC4B61A70B.jpg?width=1300&quality=90&mode=crop', 'National Park', 'Go beyond Virgin Islands National Park''s stunning white-sand beaches. Hike to historic plantation sites to learn about a challenging past when sugar and enslaved labor dominated life on the island. Visit the Indigenous Taino''s ancient petroglyphs. Snorkel coral reefs to discover hidden marine life. Two-thirds of the island of St. John is national park, making it a unique destination for visitors.', 4);
INSERT INTO main_app_placeterritory(name, image, placetype, description, territory_id)
    VALUES
        ('Virgin Islands Coral Reef', 'https://www.nps.gov/common/uploads/grid_builder/vicr/crop16_9/86DF0628-1DD8-B71B-0BA18E663750F8E8.jpg?width=1300&quality=90&mode=crop', 'National Monument', 'Virgin Islands Coral Reef National Monument includes federal submerged lands within the 3-mile belt off the island of St. John. These waters support a diverse and complex system of coral reefs and other ecosystems such as shoreline mangrove forests and seagrass beds.', 4);